function convertURLs(a){var b=[],c=[];return a.forEach(function(a){isTaobaoURL(a)?b.push(convertURL(a)):c.push(a)}),{valid:b,invalid:c}}function convertURL(a){return contains(a,M_INTL)||contains(a,H5)?buildTaobaoURL(a,ITEM_ID,!1):contains(a,WORLD)?contains(a,"item")?buildTaobaoURL(a,ITEM,!1):a.replace("world.taobao.com","taobao.com"):contains(a,SHOP_M)?buildTaobaoURL(a,SHOP_ID,!0):contains(a,"item")?buildTaobaoURL(a,"id=",!1):a.replace("m.taobao.com","taobao.com")}function getID(a,b){for(var c=a.indexOf(b)+b.length,d=a.length,e=c;e<a.length;e++)if(!isDigit(a.charAt(e))){d=e;break}return a.substring(c,d)}function buildTaobaoURL(a,b,c){if(c){var d=getID(a,b);return SHOP_URL.replace("{}",d)}var e=getID(a,b);return TAOBAO_URL+e}function isTaobaoURL(a){var b=new RegExp("^(https?:\\/\\/)?((([a-z\\d]([a-z\\d-]*[a-z\\d])*)\\.)+[a-z]{2,}|((\\d{1,3}\\.){3}\\d{1,3}))(\\:\\d+)?(\\/[-a-z\\d%_.~+]*)*(\\?[;&a-z\\d%_.~+=-]*)?(\\#[-a-z\\d_]*)?$","i");return b.test(a)&&a.indexOf("taobao.com")!=-1}function contains(a,b){return a.indexOf(b)!=-1}function isDigit(a){return a=a.charCodeAt(0),a>=48&&a<=57}const ITEM_ID="id=",ITEM="item/",SHOP_ID="shop_id=",TAOBAO_URL="https://item.taobao.com/item.htm?id=",SHOP_URL="https://shop{}.taobao.com",M_INTL="m.intl.taobao.com",H5="h5.m.taobao.com",WORLD="world.taobao.com",SHOP_M="shop.m.taobao.com";